/*import '../styles/gradient.css';

export default function AnimatedBackground() {
  return <div className="animated-bg"></div>;
}
*/
import React, { useMemo } from 'react';
import './bg.css'; // optional: if you prefer extracting css; not necessary if using global.css

export default function AnimatedBackground() {
  // create an array of particle data only once
  const particles = useMemo(() => {
    const arr = [];
    for (let i = 0; i < 18; i++) {
      arr.push({
        left: Math.random() * 100,                       // percent
        top: Math.random() * 100 + 20,                   // percent (start lower)
        size: 4 + Math.random() * 8,                     // px
        delay: Math.random() * -20,                      // negative so some are already moving
        duration: 10 + Math.random() * 22,               // seconds
        opacity: 0.06 + Math.random() * 0.18
      });
    }
    return arr;
  }, []);

  return (
    <div className="animated-bg" aria-hidden="true">
      {/* Soft gradient blobs */}
      <div className="blob b1" />
      <div className="blob b2" />
      <div className="blob b3" />

      {/* Particles layer */}
      <div className="particles">
        {particles.map((p, idx) => (
          <div
            key={idx}
            className="particle"
            style={{
              left: `${p.left}%`,
              top: `${p.top}%`,
              width: `${p.size}px`,
              height: `${p.size}px`,
              opacity: p.opacity,
              animationDuration: `${p.duration}s`,
              animationDelay: `${p.delay}s`,
            }}
          />
        ))}
      </div>
    </div>
  );
}
