import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/global.css"; // Your global styles
import AnimatedBackground from "../components/AnimatedBackground"; // Animated background component

export default function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleRegister = (e) => {
    e.preventDefault();
    if (name && email && password) {
      // For now, just navigate to login after registration
      navigate("/login");
    } else {
      alert("Please fill all fields");
    }
  };

  return (
    <div className="register-page">
      <AnimatedBackground />
      <div className="register-box">
        <img src="/images/logo.png" alt="KEYFRAMES Logo" className="logo" />
        <h2>KEYFRAMES REGISTER</h2>
        <form onSubmit={handleRegister} className="register-form">
          <div className="input-group">
            <label>Name</label>
            <input
              type="text"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label>Email</label>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="register-btn">
            Register
          </button>
        </form>
        <p>
          Already have an account?{" "}
          <span className="login-link" onClick={() => navigate("/login")}>
            Login
          </span>
        </p>
      </div>
    </div>
  );
}
